import {  useState  } from 'react'
import {  listArray } from './Arrays'
import Input from './Input'

function App() {

  const [checkedState, setCheckedState] = useState(
    new Array(listArray.length).fill(false)
);

const [checkedStateSub, setCheckedStateSub] = useState(
    new Array(listArray.length).fill(false)
);

  const handleOnChange = (props) => {
    const updatedCheckedState = checkedState.map((item, index) =>
      index === props ? !item : item
  ); 

  setCheckedState(updatedCheckedState);

  }

  const handleOnChangeSub = (props) => {
    const updatedCheckedStateSub = checkedStateSub.map((item, index) =>
      index === props ? !item : item
    );

  setCheckedStateSub(updatedCheckedStateSub)
  }


return (
    <ul className="toppings-list">
      <h1>{listArray.head}</h1> 

      {listArray.data.map(({ name, values }, index) => {
        return (

          <li key={index}>
            <div className="toppings-list-item">
              <div className="left-section">

              <Input 
                  item={name} 
                  index={index} 
                  checkedState={checkedState} 
                  handleOnChange={() => handleOnChange}
                  />      


                <section >

                  {values.map((sub) => 
                    <Input 
                    item={sub.info} 
                    index={index} 
                    checkedState= { checkedStateSub } 
                    handleOnChange={() => handleOnChangeSub}
                    /> 
                    )}
                </section>



              </div>
            </div>
          </li>      
        );
      })}
    </ul>
);
}

export default App;
