import React, { useState } from 'react';
import Input from './Input'

const Accordion = ({ title, desc, director, mainR }) => {
  const [isActive, setIsActive] = useState(false);
  const [showDir, setshowDir] = useState(false);
  const [showAct, setshowAct] = useState(false);

// const handleOnChange

  return (
    <li className="accordion-item">
      <ul className="accordion-title" onClick={() => setIsActive(!isActive)}>
      <input
        type="checkbox"
        // id={ `custom-checkbox-${props.index}`}
        name={title}
        value={title}
        checked={isActive}
        onChange={() => setIsActive(!isActive)}
        />
              <label> {title} </label>  

        <ul>{isActive ? '-' : '+'}</ul>
      </ul>
      {isActive && <ul className="accordion-content">{desc}</ul>}
      {isActive && <ul onClick={() => setshowDir(!showDir)}> Reżyseria </ul>}
      {showDir && <ul> {director} </ul>}
      {isActive && <ul onClick={() => setshowAct(!showAct)}> W rolach głównych </ul>}
      {showAct && <ul> {mainR.map((mainR) => <li>{mainR}</li>)} </ul>}
    </li>
  );
};

export default Accordion;


