import React, { useState } from 'react';
import './app.css'

const Accordion = ({ title, desc, director, mainR }) => {
  const [isActive, setIsActive] = useState(false);
  const [showDir, setshowDir] = useState(false);
  const [showAct, setshowAct] = useState(false);

  return (
    <li>
    <ul>
      <input
        type="checkbox"
        name={title}
        value={title}
        checked={isActive}
        onChange={() => setIsActive(!isActive)}
        />
              <label> {title} </label>
    </ul>  
      {isActive && <ul><p>{desc}</p></ul>}
      {isActive && 
        <ul>
          <input 
          type="checkbox" 
          name={director} 
          value={director} 
          checked={showDir}
          onChange={() => setshowDir(!showDir)}
          /> 
          <label> Reżyseria </label>  
        </ul>
        }
      {showDir && <ul className={isActive ? null : 'collapsed'}><p>{director}</p></ul>}
      {isActive && 
        <ul>
          <input 
          type="checkbox" 
          name={mainR} 
          value={mainR} 
          checked={showAct}
          onChange={() => setshowAct(!showAct)}/> 
          <label> W rolach głównych </label>  
        </ul>
        }
      {showAct && <ul className={isActive ? null : 'collapsed'}> {mainR.map((mainR) => <ul> <p>{mainR}</p> </ul>)} </ul  >}
    </li>
  );
};

export default Accordion;
