import {  useState  } from 'react'
import {  listArray } from './Arrays'

function App() {

  const [checkedState, setCheckedState] = useState(
    new Array(listArray.length).fill(false)
);

  const handleOnChange = (props, values) => {
    const updatedCheckedState = checkedState.map((item, index) =>
      index === props ? !item : item
  ); 

  {
    checkedState[props] === true ?                  
      <label htmlFor={`custom-checkbox-${props}`}>{values.map ((values) => <li>{values}</li>)} </label>
      : null
    }

  setCheckedState(updatedCheckedState);

  }


return (
  <div className="App">
    <ul className="toppings-list">
      {listArray.map(({ name, values }, index) => {
        return (
          <li key={index}>
            <div className="toppings-list-item">
              <div className="left-section">
                <input
                  type="checkbox"
                  id={`custom-checkbox-${index}`}
                  name={name}
                  value={name}
                  checked={checkedState[index]}
                  onChange={() => handleOnChange(index, values)}
                />

                <label htmlFor={`custom-checkbox-${index}`}>{name} </label>



              </div>
            </div>
          </li>      
        );
      })}
    </ul>
  </div>
);
}

export default App;
