import React, {useState} from 'react'
import {listArray} from './Arrays'


var people = [{
  id: 1,
  name: "Managing Director",
  people: [
    {
      id: 2,
      name: "Sales Director"      
    }, {
      id: 3,
      name: "IT Director",
      people: [
        {
          id: 4,
          name: "Technical Lead",
          people: [
            {
              id: 5,
              name: "Software Developer"
            },
            {
              id: 6,
              name: "Support Technician"
            }
          ]
        }        
      ]
    }, {
      id: 7,
      name: "HR Department",
      people: [
        {
          id: 8,
          name: "HR Officer",
          people: [{
            id: 9,
            name: "HR Assistant 1"
          }, {
            id: 10,
            name: "HR Assistant 2"
          }]
        }
      ]
    }
  ]
}];

function App() {    


    const nodes = listArray.data.map((name) => {
      return (
        <Node node={name} children={name.values} />
      );
    });       

    return (
      <div>
        <ul className="org">
         {nodes}
        </ul>        
      </div>
    );
  }


function Node (props) {

const [isClicked] = useState(false) 



    let childnodes = null;

    if(props.children) {      
      childnodes = props.children.map((childnode) => {
       return (
         <Node node={childnode} children={childnode} />
       );
     });
    }

    return (
      <li key={props.node.id}>      
        <span>{props.node.name}</span>        
        { childnodes ?
          <ul>{childnodes}</ul>
        : null }
      </li>
    );
  }


export default App
