import React, { useState } from 'react';
import './app.css'

const Accordion = ({ title, desc, director, mainR }) => {
  const [isActive, setIsActive] = useState(false);
  const [showDir, setshowDir] = useState(false);
  const [showAct, setshowAct] = useState(false);

  return (
    <ul>
      <ul>
    <li>
      <input
        type="checkbox"
        name={title}
        value={title}
        checked={isActive}
        onChange={() => setIsActive(!isActive)}
        />
              <label className='title'> {title} </label>
    </li> 
    </ul> 
      <ul>{isActive && <p>{desc}</p>}</ul>
      {isActive && 
        <ul className='extra'>
          <input 
          type="checkbox" 
          name={director} 
          value={director} 
          checked={showDir}
          onChange={() => setshowDir(!showDir)}
          className={showDir ? 'checked' : 'unchecked'}
          /> 
          <label> Reżyseria </label>  
        </ul>
        }
      {showDir && <ul className={isActive ? null : 'collapsed'}><div className='director'>{director}</div></ul>}
      {isActive && 
        <ul className='extra'>
          <input 
          type="checkbox" 
          name={mainR} 
          value={mainR} 
          checked={showAct}
          onChange={() => setshowAct(!showAct)}/> 
          <label> W rolach głównych </label>  
        </ul>
        }
      {showAct && <ul className={isActive ? null : 'collapsed'}> {mainR.map((mainR) => <ul> {mainR} </ul>)} </ul>}
    </ul>
  );
};

export default Accordion;
