import {  useState  } from 'react'
import {  listArray } from './Arrays'

function Nest(props) {

  const [checkedState, setCheckedState] = useState(
    new Array(listArray.length).fill(false)
);

  const handleOnChange = (props) => {
    const updatedCheckedState = checkedState.map((item, index) =>
      index === props ? !item : item
  ); 
  
  

  setCheckedState(updatedCheckedState);

  }


return (
  <div className="App">
    <ul className="toppings-list">
      {listArray.map(({ values }, props) => {
        return (
          <li key={index}>
            <div className="toppings-list-item">
              <div className="left-section">
                <input
                  type="checkbox"
                  id={`custom-checkbox-${props}`}
                  name={values}
                  value={values}
                  checked={checkedState[props]}
                  onChange={() => handleOnChange(props)}
                />
                
                <label htmlFor={`custom-checkbox-${props}`}>{values} </label>
                
              </div>
            </div>
          </li>      
        );
      })}
    </ul>
  </div>
);
}

export default Nest;
