import React, { useState } from 'react';
import './app.css'

const Accordion = ({ title, desc, director, mainR }) => {
  const [isActive, setIsActive] = useState(false);
  const [showDir, setshowDir] = useState(false);
  const [showAct, setshowAct] = useState(false);

// const handleOnChange

  return (
    <li>
      <input
        type="checkbox"
        // id={ `custom-checkbox-${props.index}`}
        name={title}
        value={title}
        checked={isActive}
        onChange={() => setIsActive(!isActive)}
        />
              <label> {title} </label>  
      {isActive && <p className="accordion-content">{desc}</p>}
      {isActive && 
        <div>
          <input 
          type="checkbox" 
          name={director} 
          value={director} 
          checked={showDir}
          onChange={() => setshowDir(!showDir)}/> 
          <label> Reżyseria </label>  
        </div>
          }
      {showDir && <ul> {director} </ul>}
      {isActive && <div>
          <input 
          type="checkbox" 
          name={mainR} 
          value={mainR} 
          checked={showAct}
          onChange={() => setshowAct(!showAct)}/> 
          <label> W rolach głównych </label>  
        </div>}
      {showAct && <ul> {mainR.map((mainR) => <li>{mainR}</li>)} </ul>}
    </li>
  );
};

export default Accordion;
