import React, { useState } from 'react';
import './app.css'

const Accordion = ({ title, desc, director, mainR }) => {
  const [isActive, setIsActive] = useState(false);
  const [showDesc, setshowDesc] = useState(false);
  const [showDir, setshowDir] = useState(false);
  const [showAct, setshowAct] = useState(false);

  return (
    <ul>
    <li>
      <input
        type="checkbox"
        name={title}
        value={title}
        checked={isActive}
        onChange={() => setIsActive(!isActive)}
        />
        <label className='title'> {title} </label>
    </li> 
    <li>
    {isActive &&
      <ul>
      <input
        type="checkbox"
        name={desc}
        value={desc}
        checked={showDesc}
        onChange={() => setshowDesc(!showDesc)}
        className={showDesc ? 'checked' : 'unchecked'}
        />
        <label> Pokaż opis </label>
      </ul> 
}
</li>
      <ul>{showDesc && <p>{desc}</p>}</ul>
      <li>
      {isActive && 
        <ul>
          <input 
          type="checkbox" 
          name={director} 
          value={director} 
          checked={showDir}
          onChange={() => setshowDir(!showDir)}
          className={showDir ? 'checked' : 'unchecked'}
          /> 
          <label> Reżyseria </label>  
        </ul>
        }
        </li>
      {showDir && <li className={isActive ? null : 'collapsed'}>{director}</li>}
      <li>
      {isActive && 
        <ul>
          <input 
          type="checkbox" 
          name={mainR} 
          value={mainR} 
          checked={showAct}
          onChange={() => setshowAct(!showAct)}/> 
          <label> W rolach głównych </label>  
        </ul>
        }
      </li>
      {showAct && <ul className={isActive ? null : 'collapsed'}> {mainR.map((mainR) => <ul> {mainR} </ul>)} </ul>}
    </ul>
  );
};

export default Accordion;
