import React, { useState } from 'react';
import './app.css'

const Accordion = ({ title, desc, director, mainR }) => {
  const [isActive, setIsActive] = useState(false);
  const [showDesc, setshowDesc] = useState(false);
  const [showDir, setshowDir] = useState(false);
  const [showAct, setshowAct] = useState(false);

  return (
    <ul>
    
    <li>
      <input
      type="checkbox"
      name={title}
      value={title}
      checked={isActive}
      onChange={() => setIsActive(!isActive)}
    />
    <label className='title'> {title} </label> 
    </li>
    {isActive &&
      <li>
      <input
        type="checkbox"
        name={desc}
        value={desc}
        checked={showDesc}
        onChange={() => setshowDesc(!showDesc)}
        />
        <label> Pokaż opis </label>
      </li> 
}
      {showDesc && <li className={isActive ? null : 'collapsed'}>{desc}</li>}
      {isActive && 
        <li>
          <input 
          type="checkbox" 
          name={director} 
          value={director} 
          checked={showDir}
          onChange={() => setshowDir(!showDir)}
          /> 
          <label> Reżyseria </label>  
        </li>
        }
      {showDir && <li className={isActive ? null : 'collapsed'}>{director}</li>}
      {isActive && 
        <li>
          <input 
          type="checkbox" 
          name={mainR} 
          value={mainR} 
          checked={showAct}
          onChange={() => setshowAct(!showAct)}/> 
          <label> W rolach głównych </label>  
        </li>
        }
      {showAct && <li className={isActive ? null : 'collapsed'}> {mainR.map((mainR) => <li> {mainR} </li>)} </li>}
    </ul>
  );
};

export default Accordion;
