export const listArray = [
    {   name: "jajko", values:  ["na twardo", "na miękko", "jajecznica", "s"]},
    {   name: "cebulka", values:  ["na twardo", "na x", "jajecznica", "sadzone"] },
    {   name: "herbata", values:  ["na twardo", "na miękko", "jajecznica", "sadzone"] },
    {   name: "kawusia", values:  ["na z", "na miękko", "jajecznica", "xx"] },
    {   name: "kawusia", values:  ["na z", "na miękko", "jajecznica", "xx"] },
    {   name: "kawusia", values:  ["na z", "na miękko", "jajecznica", "xx"] }
  ];

export const MapValues = ( checkedState, handleOnChange ) => {
<ul className="toppings-list">
      {listArray.map(({ values }, index) => {
        return (
          <li key={index}>
            <div className="toppings-list-item">
              <div className="left-section">
                <input
                  type="checkbox"
                  id={`custom-checkbox-${index}`}
                  name={values}
                  value={values}
                  checked={checkedState[index]}
                  onChange={() => handleOnChange(index)}
                />
</div>
</div>
</li>
   );
  })}
</ul>
}
